import React from 'react';
import { Button, Card } from 'react-bootstrap';

const CartItem = ({ game, onRemove, onQuantityChange }) => {
  return (
    <Card className="mb-3">
      <Card.Img variant="top" src={game.imageUrl} alt={game.name} />
      <Card.Body>
        <Card.Title>{game.name}</Card.Title>
        <Card.Text>
          Price: {game.price}
        </Card.Text>
        <div className="d-flex align-items-center">
          <Button variant="outline-primary" onClick={() => onQuantityChange(game, -1)}>-</Button>
          <span className="mx-2">{game.quantity}</span>
          <Button variant="outline-primary" onClick={() => onQuantityChange(game, 1)}>+</Button>
        </div>
        <Button variant="danger" onClick={() => onRemove(game)}>Remove</Button>
      </Card.Body>
    </Card>
  );
};

export default CartItem;
